import React, { useState, useEffect } from "react";

const Range = () => {
	const [data, setData] = useState(0);
	console.log(data);

	return (
		<div>
			<input
				type="range"
				min="0"
				max="100"
				step="10"
				onChange={(event) => setData(event.target.value)}
			/>
			<h1>{data}</h1>
		</div>
	);
};

export default Range;
